# 创建平台银行账户
def create_platform_account(bank_name=None, bank_card_number=None, real_name=None, balance=0,
                            withdrawable_balance=0, alipay_balance=0, alipay_withdrawable_balance=0):
    from common.models import Account
    Account.objects.create(
        bank_name=bank_name,  # 开户行
        bank_card_number = bank_card_number,  # 银行卡号
        real_name = real_name,  # 真实姓名
        balance = balance, # 余额
        withdrawable_balance = withdrawable_balance,  # 可提现余额
        alipay_balance=alipay_balance,
        alipay_withdrawable_balance=alipay_withdrawable_balance
    )


# 创建后台管理员
def create_system_admin(username, password, name):
    from common.models import SystemAdmin, LoginStats
    from common.password_backend import make_password
    from config import SYSTEM_USER_STATUS

    login_stats = LoginStats.objects.create(
        last_success_login = None,  # 上次成功登录时间
        last_success_ip = None,  # 上次成功登录IP
        last_failed_login = None,  # 上次失败登录时间
        last_failed_ip = None, # 上次失败登录IP
        failed_login_count = 0
    )
    SystemAdmin.objects.create(
        username=username,  # 用户名
        password = make_password(password) , # 密码
        name = name , # 姓名
        status = SYSTEM_USER_STATUS.USING,  # 状态
        is_super = True,  # 是否是超级管理员
        permissions = '',  # 权限
        login_stats = login_stats,  # 登录情况统计
    )


# 创建商户分类
def create_merchant_category():
    from common.models import MerchantCategory

    for parent, children in merchant_category.items():
        p = MerchantCategory.objects.create(name=parent, parent=None)
        for sub in children:
            MerchantCategory.objects.create(name=sub, parent=p)


# 创建城市和街道
def create_city_and_area(city_qu, qu_jie):
    from common.models import Area, City

    for shi, qu in city_qu.items():
        c = City.objects.create(name=shi)
        for qu_name, qu_info in qu.items():
            area_p = Area.objects.create(name=qu_name, parent=None, city=c, adcode=qu_info[1])
            jie_list = qu_jie[qu_name]
            for jie in jie_list:
                area_sub = Area.objects.create(name=jie[0], parent=area_p, city=c, adcode=jie[1])


